static unsigned long readJpg(char *name, char **data){
    FILE *file;
	unsigned long size;
    file = fopen(name, "rb");
    if (!file)
    {
        fprintf(stderr, "Unable to open file %s", name);
        return 1;
    }
    
    //Get file length
    fseek(file, 0, SEEK_END);
    size=ftell(file);
    fseek(file, 0, SEEK_SET);
    printf("size %ld\n",size);
    //Allocate memory
    *data=(char *)malloc(size+1);
    if (!*data)
    {
        fprintf(stderr, "Memory error!");
        fclose(file);
        return 1;
    }
    fread(*data, size, 1, file);
    printf("%x %x %x\n",(unsigned char)(*data)[0],(unsigned char)(*data)[1],(unsigned char)(*data)[2]);
    fclose(file);
    return size;
}


int main()
{
    // photo_locations_t photo_locations;
    // get_photo_locations_from_design(SVGFILE,&photo_locations);
    unsigned char* capture = IMAGE_DATA;
    design_t design = {0,0,NULL,NULL,NULL};
    load_design_from_file(&design, SVGFILE);
    unsigned char ** captures;
    captures = malloc(design.total_photos*sizeof(char*));
    for(int i = 0; i < design.total_photos;i++){
        captures[i]= capture;
    }
    render_design(&design,captures);
    free(captures);
    free_design(&design);

    char * gp_jpeg_data;
    unsigned long size = readJpg("9pr.jpg",&gp_jpeg_data);

    int encoded_size = 2*size;
    char *out = (char*)malloc(encoded_size);
    strcpy(out,"data:image/jpeg;base64,");
    /* keep track of our encoded position */
    char* c = out+23;
    /* store the number of bytes encoded by a single call */
    int cnt = 0;
    /* we need an encoder state */
    base64_encodestate s;

    base64_init_encodestate(&s);
    cnt = base64_encode_block(gp_jpeg_data,size, c, &s);
    c += cnt;
    cnt = base64_encode_blockend(c, &s);
    c += cnt;
    *c=0;
    FILE *fp = fopen("base64.txt", "w");
    fprintf(fp,"%s",out);
    fclose(fp);

    free(out);
    free(gp_jpeg_data);
    return 0;
}